mapper.select:
select userid, username, userpwd, email, address, joindate
from ssafy_member
where userid = #{userid} and userpwd = #{userpwd}

select username, userid, userpwd, email, address, joindate
from ssafy_member
where userid = #{userid}

mapper.update:
update ssafy_member
userpwd = #{userpwd}, email = #{email}, address = #{address},
where userid = #{userid}
