mapper.select:
select no, title, writer, content, regtime,views
  from notice_board 
 where no = #{no}

mapper.insert:
insert into notice_board(
	title, writer, content, regtime
) values (
	#{title}, #{writer}, #{content}, now()
)

mapper.update:
update notice_board 
   set title = #{title}, 
   	   writer = #{writer}, 
   	   content = #{content}
 where no = #{no}

update notice_board 
   set views = views +1
 where no = #{no}
